mapper.select:
SELECT COUNT(*) FROM tb_board_question q


		AND q.content like concat('%',#{query},'%' )



		AND q.location = #{location}



		AND q.tag = #{tag}

SELECT q.id, u.id, u.nickname, q.memberId, q.tag, q.content, q.location, q.title, q.inserted, q.updated FROM tb_user u JOIN tb_board_question q ON u.id = q.memberId
AND q.content like concat('%',#{query},'%' )
AND q.location = #{location}
AND q.tag = #{tag}
GROUP BY q.id ORDER BY q.id DESC LIMIT #{from}, #{items}

SELECT
q.id, q.memberId, q.title, q.content, q.tag, q.views, q.inserted, q.location, q.updated, u.nickname FROM tb_board_question q JOIN tb_user u ON q.memberId = u.id LEFT JOIN tb_reply_question r ON q.id = r.boardId
WHERE q.id = #{id}
